import React, { Component } from 'react';
import axios from 'axios';
import FileBase64 from 'react-file-base64';
import updateImg from './update.png';
import companyLogo from './logo_[Black Edition]_ICO.png';

export default class FoodItemEdit extends Component {


  constructor(props){
    super(props);
    this.state={
        ItemName:"",
        MCategory:"",
        SubCategory:"",
        ItemUnitPrice:"",
        ItemImg:""
    }
}

//handleInputChange  <----------------------------------------------
handleInputChange = (e) => {
    const {name,value} = e.target;

    this.setState({
        ...this.state,
        [name]:value
    })
}


onSubmit = (e) => {
    e.preventDefault();
    const id = this.props.match.params.id;
    const {ItemName,MCategory,SubCategory,ItemUnitPrice,ItemImg} = this.state;

    const data = {
        ItemName:ItemName,
        MCategory:MCategory,
        SubCategory:SubCategory,
        ItemUnitPrice:ItemUnitPrice,
        ItemImg:ItemImg
    }

    console.log(data)

    axios.put(`/post/update/${id}`,data).then((res) => {
        if(res.data.success){
          alert("Item Updated Succesfully!")
            this.setState( 
                {
                    ItemName:"",
                    MCategory:"",
                    SubCategory:"",
                    ItemUnitPrice:"",
                    ItemImg:""
                })
        }
    })
}




  componentDidMount(){
    const id = this.props.match.params.id;

    axios.get(`/post/${id}`).then((res) => {
        if(res.data.success){
            this.setState({
              ItemName:res.data.post.ItemName,
              MCategory:res.data.post.MCategory,
              SubCategory:res.data.post.SubCategory,
              ItemUnitPrice:res.data.post.ItemUnitPrice,
              ItemImg:res.data.post.ItemImg
