import React, { Component } from 'react';
import axios from 'axios';

export default class EditPost extends Component {


  constructor(props){
    super(props);
    this.state={
        ItemName:"",
        MCategory:"",
        SubCategory:"",
        ItemUnitPrice:"",
        ItemImg:""
    }
}

//handleInputChange  <----------------------------------------------
handleInputChange = (e) => {
    const {name,value} = e.target;

    this.setState({
        ...this.state,
        [name]:value
    })
}


onSubmit = (e) => {
    e.preventDefault();
    const id = this.props.match.params.id;
    const {ItemName,MCategory,SubCategory,ItemUnitPrice,ItemImg} = this.state;

    const data = {
        ItemName:ItemName,
        MCategory:MCategory,
        SubCategory:SubCategory,
        ItemUnitPrice:ItemUnitPrice,
        ItemImg:ItemImg
    }

    console.log(data)

    axios.put(`/post/update/${id}`,data).then((res) => {
        if(res.data.success){
          alert("")
            this.setState( 
                {
                    ItemName:"",
                    MCategory:"",
                    SubCategory:"",
                    ItemUnitPrice:"",
                    ItemImg:""
                })
        }
    })
}




  componentDidMount(){
    const id = this.props.match.params.id;

    axios.get(`/post/${id}`).then((res) => {
        if(res.data.success){
            this.setState({
                post:res.data.post
            });

            console.log(this.state.post);
        }
    });
  }



  render() {
    return (

      <div className="container_addItem_POPUP">
      <div className="imgContainer">
          <img src={additemImg} alt='ItemLOGO'/>
      </div>
      
      <div className="formContainer">
          <h1 className="addItem_h1"><u>ADD NEW ITEM</u></h1>
          <form className="addItem_form" noValidate>


              <div className="addItem_form_group">
                  <label>Item Name</label>
                  <input type="text"
                  className="addItem_input"
                  name="ItemName"
                  placeHolder="Enter New Item Name"
                  value={this.state.ItemName}
                  onChange={this.handleInputChange} />
              </div>


              <div className="addItem_form_group">
                  <label>Main Category</label>
                  <input type="text"
                  className="addItem_input"
                  name="MCategory"
                  placeHolder="Enter New Main Category"
                  value={this.state.MCategory}
                  onChange={this.handleInputChange} />
              </div>


              <div className="addItem_form_group">
                  <label>Sub Category</label>
                  <input type="text"
                  className="addItem_input"
                  name="SubCategory"
                  placeHolder="Enter Sub Category"
                  value={this.state.SubCategory}
                  onChange={this.handleInputChange} />
              </div>


              <div className="addItem_form_group">
                  <label>Item Unit Price</label>
                  <input type="text"
                  className="addItem_input"
                  name="ItemUnitPrice"
                  placeHolder="Enter New Item Unit Price"
                  value={this.state.ItemUnitPrice}
                  onChange={this.handleInputChange} />
              </div>


              <div className="addItem_form_group">
                  <label>Item Image</label>
                  <input type="text"
                  className="addItem_input"
                  name="ItemImg"
                  placeHolder="Enter New Item Image"
                  value={this.state.ItemImg}
                  onChange={this.handleInputChange} />
              </div>


              <button className="addItem_Btn" type="submit" onClick={this.onSubmit}>save</button>

          </form>
      </div>
      


      
  </div>

    )
  }
}
