import React, { Component } from 'react';
import axios from 'axios';
import companyLogo from './logo_[Black Edition]_ICO.png';


export default class PostDetails extends Component {
    constructor(props){
        super(props);

        this.state={
            post:{}
        };
    }

    componentDidMount(){
        const id = this.props.match.params.id;

        axios.get(`/post/${id}`).then((res) => {
            if(res.data.success){
                this.setState({
                    post:res.data.post
                });

                console.log(this.state.post);
            }
        });
    }

  render() {

    const {ItemName,MCategory,SubCategory,ItemUnitPrice,ItemImg} = this.state.post;

    return (

        <div>





        
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">

                <a className="navbar-brand" href="/" style={{color:"green"}}><img src={companyLogo} alt="logo"/></a>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>

            </div>
        </nav>








        <div className="postdetails_container" style={{marginTop:'40px'}}>
            <h4>{ItemName}</h4>
            <hr/>

            <dl className="row">
                <dt className="col1">Main Category</dt>
                <dd className="col2">{MCategory}</dd>

                <dt className="col1">Sub Category</dt>
                <dd className="col2">{SubCategory}</dd>

                <dt className="col1">Item Unit Price</dt>
                <dd className="col2">Rs.{ItemUnitPrice}/=</dd>

                <dt className="col1">Item Image</dt>
                <dd className="col2">{ItemImg}</dd>
            </dl>
            
        </div> 


        </div>

         

    )
  }
}
