import React, { Component } from 'react';
import axios from 'axios';
import additemImg from './additemImg.png';
import companyLogo from './logo_[Black Edition]_ICO.png';

export default class CreatePost extends Component {

    constructor(props){
        super(props);
        this.state={
            ItemName:"",
            MCategory:"",
            SubCategory:"",
            ItemUnitPrice:"",
            ItemImg:DefaultImg
        }
    }


    setDefaultImage(uploadType) {
        if (uploadType === "multer") {
            this.setState({
                Item
            })
        }
    }

    //handleInputChange  <----------------------------------------------
    handleInputChange = (e) => {
        const {name,value} = e.target;

        this.setState({
            ...this.state,
            [name]:value
        })
    }

    

    


    onSubmit = (e) => {
        
        const {ItemName,MCategory,SubCategory,ItemUnitPrice,setFileName} = this.state;

        const onChangeFile = (e) => {
            setFileName(e.target.files[0]);
        };



        e.preventDefault();
        

        const formData = new FormData();

        formData.append("ItemName",ItemName);
        formData.append("MCategory",MCategory);
        formData.append("SubCategory",SubCategory);
        formData.append("ItemUnitPrice",ItemUnitPrice);
        formData.append("ItemImg",setFileName);

  /*      const data = {
            ItemName:ItemName,
            MCategory:MCategory,
            SubCategory:SubCategory,
            ItemUnitPrice:ItemUnitPrice,
            ItemImg:ItemImg
        }
*/





        console.log(formData)

        axios.post("/post/save",formData).then((res) => {
            if(res.formData.success){
                alert("Item Added Succesfully!")
                this.setState( 
                    {
                        ItemName:"",
                        MCategory:"",
                        SubCategory:"",
                        ItemUnitPrice:"",
                        ItemImg:""
                    })
 
            }
        })

        

    }

    


  render() {
    return (


        <div>





        
        <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">

                <a className="navbar-brand" href="/" style={{color:"green"}}><img src={companyLogo} alt="logo"/></a>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>

            </div>
        </nav>






        <div className="container_addItem_POPUP">
            <div className="imgContainer">
                <img src={additemImg} alt='ItemLOGO'/>
            </div>
            
            <div className="formContainer">
                <h1 className="addItem_h1"><u>ADD NEW ITEM</u></h1>
                <form className="addItem_form" onSubmit={this.onSubmit} encType="multipart/form-data">


                    <div className="addItem_form_group">
                        <label>Item Name</label>
                        <input type="text"
                        className="addItem_input"
                        name="ItemName"
                        placeHolder="Enter New Item Name"
                        value={this.state.ItemName}
                        onChange={this.handleInputChange} />
                    </div>


                    <div className="addItem_form_group">
                        <label>Main Category</label>
                        <input type="text"
                        className="addItem_input"
                        name="MCategory"
                        placeHolder="Enter New Main Category"
                        value={this.state.MCategory}
                        onChange={this.handleInputChange} />
                    </div>


                    <div className="addItem_form_group">
                        <label>Sub Category</label>
                        <input type="text"
                        className="addItem_input"
                        name="SubCategory"
                        placeHolder="Enter Sub Category"
                        value={this.state.SubCategory}
                        onChange={this.handleInputChange} />
                    </div>


                    <div className="addItem_form_group">
                        <label>Item Unit Price(Rs.)</label>
                        <input type="text"
                        className="addItem_input"
                        name="ItemUnitPrice"
                        placeHolder="Enter New Item Unit Price"
                        value={this.state.ItemUnitPrice}
                        onChange={this.handleInputChange} />
                    </div>


        {/*            <div className="addItem_form_group">
                        <label>Item Image</label>
                        <input type="text"
                        className="addItem_input"
                        name="ItemImg"
                        placeHolder="Enter New Item Image"
                        value={this.state.ItemImg}
                        onChange={this.handleInputChange} />
                    </div>
    */}


                    <div className="addItem_form_group">
                        <label htmlFor="file">Choose Item Image</label>
                        <input type="file"
                        name="ItemImg"
                        className="#"
                        onChange={(e) => this.uploadImage(e, "multer")}/>
                        <img src={this.state.ItemImg} alt="upload-image" className="uploadingImgSrc"/>
                    </div>


                    <button className="addItem_Btn" type="submit" onClick={this.onSubmit}>save</button>

                </form>
            </div>
            
        </div>



        </div>

        

    )
  }
}
