import React, { Component } from 'react';
import axios from 'axios';
import updateImg from './update.png';
import companyLogo from './logo_[Black Edition]_ICO.png';

export default class EditPost extends Component {


  constructor(props){
    super(props);
    this.state={
        ItemName:"",
        MCategory:"",
        SubCategory:"",
        ItemUnitPrice:"",
        ItemImg:""
    }
}

//handleInputChange  <----------------------------------------------
handleInputChange = (e) => {
    const {name,value} = e.target;

    this.setState({
        ...this.state,
        [name]:value
    })
}


onSubmit = (e) => {
    e.preventDefault();
    const id = this.props.match.params.id;
    const {ItemName,MCategory,SubCategory,ItemUnitPrice,ItemImg} = this.state;

    const data = {
        ItemName:ItemName,
        MCategory:MCategory,
        SubCategory:SubCategory,
        ItemUnitPrice:ItemUnitPrice,
        ItemImg:ItemImg
    }

    console.log(data)

    axios.put(`/post/update/${id}`,data).then((res) => {
        if(res.data.success){
          alert("Post Updated Succesfully!")
            this.setState( 
                {
                    ItemName:"",
                    MCategory:"",
                    SubCategory:"",
                    ItemUnitPrice:"",
                    ItemImg:""
                })
        }
    })
}




  componentDidMount(){
    const id = this.props.match.params.id;

    axios.get(`/post/${id}`).then((res) => {
        if(res.data.success){
            this.setState({
              ItemName:res.data.post.ItemName,
              MCategory:res.data.post.MCategory,
              SubCategory:res.data.post.SubCategory,
              ItemUnitPrice:res.data.post.ItemUnitPrice,
              ItemImg:res.data.post.ItemImg
            });

            console.log(this.state.post);
        }
    });
  }



  render() {
    return (


      <div>




        
<       nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">

                <a className="navbar-brand" href="/" style={{color:"green"}}><img src={companyLogo} alt="logo"/></a>
                <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>


                <div className="collapse navbar-collapse" id="navbarSupportedContent">
                    <form className="d-flex">
                        <input id="searchNav" className="form-control me-2" type="search" placeholder="Search" aria-label="Search" onChange={this.handleSearchArea}/>
                    </form>
                </div>

            </div>
        </nav>







        <div className="container_addItem_POPUP">

            <div className="imgContainer">
                <img src={updateImg} alt='updateItemLOGO'/>
            </div>
            
            <div className="formContainer">
                <h1 className="addItem_h1"><u>EDIT ITEM</u></h1>
                <form className="addItem_form" noValidate>


                    <div className="addItem_form_group">
                        <label>Item Name</label>
                        <input type="text"
                        className="addItem_input"
                        name="ItemName"
                        placeHolder="Enter New Item Name"
                        value={this.state.ItemName}
                        onChange={this.handleInputChange} />
                    </div>


                    <div className="addItem_form_group">
                        <label>Main Category</label>
                        <input type="text"
                        className="addItem_input"
                        name="MCategory"
                        placeHolder="Enter New Main Category"
                        value={this.state.MCategory}
                        onChange={this.handleInputChange} />
                    </div>


                    <div className="addItem_form_group">
                        <label>Sub Category</label>
                        <input type="text"
                        className="addItem_input"
                        name="SubCategory"
                        placeHolder="Enter Sub Category"
                        value={this.state.SubCategory}
                        onChange={this.handleInputChange} />
                    </div>


                    <div className="addItem_form_group">
                        <label>Item Unit Price(Rs./=)</label>
                        <input type="text"
                        className="addItem_input"
                        name="ItemUnitPrice"
                        placeHolder="Enter New Item Unit Price"
                        value={this.state.ItemUnitPrice}
                        onChange={this.handleInputChange} />
                    </div>


                    <div className="addItem_form_group">
                        <label>Item Image</label>
                        <input type="text"
                        className="addItem_input"
                        name="ItemImg"
                        placeHolder="Enter New Item Image"
                        value={this.state.ItemImg}
                        onChange={this.handleInputChange} />
                    </div>


                    <button className="addItem_Btn" type="submit" onClick={this.onSubmit}>Update</button>

                </form>
            </div>
            


            
        </div>





      </div>




      

    )
  }
}
